import sysimport numpy as npfrom scipy.fft import fftfrom scipy.io import wavfiledef makeFilePath(filename):    return '/Users/nathanwolf/Documents/coding/PycharmProjects/bop predictor with flask/audio files/' + filenamedef getBass(audioData: np.ndarray):    bass = np.abs(audioData)[:round((400 / 20000) * len(audioData))]    bassNum = sum(sum(bass))    bassRating = round((bassNum / sum(sum(np.abs(audioData)))) * 235.5 + 1.38)    if bassRating > 5:        bassRating = 5    if bassRating < 1:        bassRating = 1    print(f"BASS: {bassRating}\n")    return bassRatingdef getVocal(audioData: np.ndarray):    vocal = np.abs(audioData)[round((85 / 20000) * len(audioData)):round((255 / 20000) * len(audioData))]    vocalNum = sum(sum(vocal))    vocalRating = round((vocalNum / sum(sum(np.abs(audioData)))) * -922) + 9    if vocalRating > 5:        vocalRating = 5    if vocalRating < 1:        vocalRating = 1    print(f"VOCAL: {vocalRating}\n")    return vocalRatingdef getOriginality(audioData: np.ndarray):    rate, data = wavfile.read('/Users/nathanwolf/Documents/coding/PycharmProjects/tryGUI/21 Savage - Bank Account (Official Audio)-WAV.wav')    bankAccount = fft(data)    if len(audioData)>=len(bankAccount):        truncatedSong = audioData[:len(bankAccount)]    else:        bankAccount=bankAccount[:len(audioData)]        truncatedSong=audioData    sumDiffs = sum(abs(sum(bankAccount - truncatedSong)))    #TODO: take the current values(sumdiffs*3.27...) and plug them into this formula:    #.000137x^2-.149x+4.71    valToBeAdjusted=round(sumDiffs*(3.26997174e-7))    print(f"ORIGINALITY: {round(.73*((.00137*valToBeAdjusted**2)-(.149*valToBeAdjusted)+4.71)+1.38)}\n")    return round(.73*((.00137*valToBeAdjusted**2)-(.149*valToBeAdjusted)+4.71)+1.38)def main(fileName:str):    rate, data = wavfile.read(makeFilePath(fileName))    fft_out = fft(data)    bass = getBass(fft_out)    vocal = getVocal(fft_out)    originality = getOriginality(fft_out)    return bass,vocal,originality